import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonSlurper
import groovy.json.JsonOutput

def flowFile = session.get()
if (!flowFile) return

try {

    // ==== C JSON INPUT ====
    def jsonText = ""
    session.read(flowFile, { inputStream ->
        jsonText = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    } as org.apache.nifi.processor.io.InputStreamCallback)

    // ==== PARSE JSON ====
    def inputJson
    try {
        inputJson = new JsonSlurper().parseText(jsonText)
    } catch (Exception e) {
        throw new RuntimeException("JSON khng hp l: " + e.message)
    }

    // ==== LY BASE64 CONTENT ====
    def base64Content = inputJson.file_Content ?: ""

    // ==== BUILD JSON PAYLOAD CHO KAFKA ====
    def jsonData = [
        value: [
            type: "STRING",
            data: base64Content
        ]
    ]

    def outputJson = JsonOutput.toJson(jsonData)

    // ==== WRITE JSON OUTPUT ====
    flowFile = session.write(flowFile, { outputStream ->
        outputStream.write(outputJson.getBytes(StandardCharsets.UTF_8))
    } as org.apache.nifi.processor.io.OutputStreamCallback)

    // ==== SET HEADERS ====
    flowFile = session.putAttribute(flowFile, "mime.type", "application/json")
    flowFile = session.putAttribute(flowFile, "Content-Type", "application/json")

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception ex) {
    log.error("Li x l JSON Base64", ex)
    flowFile = session.putAttribute(flowFile, "error.message", ex.message)
    session.transfer(flowFile, REL_FAILURE)
}

import groovy.json.JsonSlurper
import groovy.xml.StreamingMarkupBuilder
