import java.nio.charset.StandardCharsets

def flowFile = session.get()
if (!flowFile) return

try {
    flowFile = session.write(flowFile, { inputStream, outputStream ->

        // =======================
        // 1. c JSON
        // =======================
        def jsonText = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
        def json = new JsonSlurper().parseText(jsonText)

        // =======================
        // 2. HM CHUN HA MNG
        // =======================
        def ensureArray = { raw ->
            if (raw == null) return [[:]]

            if (raw instanceof List) {
                return raw.isEmpty() ? [[:]] : raw
            }

            if (raw instanceof Map) {
                return [raw]
            }

            if (raw instanceof String && raw.trim()) {
                try {
                    def parsed = new JsonSlurper().parseText(raw)
                    if (parsed instanceof List) {
                        return parsed.isEmpty() ? [[:]] : parsed
                    }
                    if (parsed instanceof Map) {
                        return [parsed]
                    }
                } catch (ignored) {}
            }

            return [[:]]
        }

        // =======================
        // 3. XML Builder
        // =======================
        def builder = new StreamingMarkupBuilder()
        builder.encoding = "UTF-8"

        def xml = builder.bind {
            mkp.xmlDeclaration()
            Root {

                // ===== NguonDuLieu =====
                NguonDuLieu {
                    IdNguonDuLieu(json.IdNguonDuLieu ?: "")
                    MaLoaiDuLieu(json.MaLoaiDuLieu ?: "")
                }

                // ===== TrangThaiDuLieu =====
                TrangThaiDuLieu {
