                    LoaiSuKien(json.LoaiSuKien ?: "")
                    MoTa {
                        LyDoCapNhat(json.MoTa_LyDoCapNhat ?: "")
                        ChiTietLyDoCapNhat(json.MoTa_ChiTietLyDoCapNhat ?: "")
