                // ===== DuLieuTiepNhan =====
                DuLieuTiepNhan {
                    IdBanGhi(json.IdBanGhi ?: "")

                    // ===== ThongTinToChuc =====
                    ThongTinToChuc {
                        MaDDTC(json.ToChuc_MaDDTC ?: "")
                        MaSoThue(json.ToChuc_MaSoThue ?: "")

                        ThongTinDiaChiTruSo {
