                            MaXa(json.ToChuc_DiaChiTruSo_MaXa ?: "")
                            PhuongXa(json.ToChuc_DiaChiTruSo_PhuongXa ?: "")
                            MaTinh(json.ToChuc_DiaChiTruSo_MaTinh ?: "")
                            TinhTP(json.ToChuc_DiaChiTruSo_TinhTP ?: "")
                            QuocGia(json.ToChuc_DiaChiTruSo_QuocGia ?: "")
                        }

                        // ===== DSMoiQuanHeVoiCaNhan =====
                        def listMQH = ensureArray(json.DSMoiQuanHeVoiCaNhan)
                        DSMoiQuanHeVoiCaNhan {
                            listMQH.each { mqh ->
                                MoiQuanHeVoiCaNhan {
