                            ]
                        ],

                        DSTinhTietTangNang: [
                            _array: "QuyetDinhXuPhat_DSTinhTietTangNang",
                            _item: "TinhTietTangNang",
                            fields: [
                                MaTinhTietTangNang: "MaTinhTietTangNang",
                                TenTinhTietTangNang: "TenTinhTietTangNang"
                            ]
                        ],

                        TrangThai: "QuyetDinhXuPhat_TrangThai"
                    ]
                ]
            ]
        ]

        // -------------------- HM BUILD XML (GI NGUYN) --------------------------
        def buildXml
        buildXml = { builder, map, jsonObj ->

            map.each { tag, value ->

                if (value instanceof Map) {

                    // ARRAY HANDLER
                    if (value._array) {

                        def arrText = jsonObj[value._array]
                        def arr = []

                        try { arr = arrText ? new JsonSlurper().parseText(arrText) : [] }
                        catch (e) { arr = [] }

                        builder."$tag" {

                            // STRING ARRAY
                            if (value._stringArray) {
                                if (arr.isEmpty()) builder."${value._item}"("")
                                else arr.each { v -> builder."${value._item}"(v ?: "") }
                            }

                            // SPECIAL EMPTY OBJECT CASES
                            else if (["DSTinhTietGiamNhe", "DSTinhTietTangNang",
                                      "DSBienPhapKhacPhucHauQua",
                                      "DSGiayToDinhDanhNN", "DSGiayToDinhDanhCN","DSHanhViViPhamHC","DSHinhThucXuPhatBoSung","DSQuocTich"]
                                      .contains(tag)
                                      && arr.isEmpty()) {

                                builder."${value._item}" {
                                    value.fields.each { ftag, fjson ->
                                        builder."$ftag"("")
                                    }
                                }
                            }

                            // NORMAL ARRAY OBJECT
                            else {
                                arr.each { item ->
                                    builder."${value._item}" {
                                        value.fields.each { ftag, fjson ->
                                            builder."$ftag"(fjson ? item[fjson] ?: "" : "")
                                        }
                                    }
                                }
                            }
                        }
                    }

                    // NORMAL OBJECT
                    else {
                        builder."$tag" {
                            buildXml(builder, value, jsonObj)
                        }
                    }

                } else {
                    // LEAF NODE
                    builder."$tag"(jsonObj[value] ?: "")
                }
            }
        }

        // ---------------- OUTPUT XML -------------------
        def writer = new StringWriter()
        def xml = new MarkupBuilder(writer)
        xml.mkp.xmlDeclaration(version: "1.0", encoding: "UTF-8")

        buildXml(xml, xmlMapping, json)

        outputStream.write(writer.toString().getBytes(StandardCharsets.UTF_8))

    } as org.apache.nifi.processor.io.StreamCallback)

    // Success
    flowFile = session.putAttribute(flowFile, "mime.type", "application/xml")
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception ex) {

    log.error("LI JSON KHNG HP L: " + ex.message)

    flowFile = session.putAttribute(flowFile, "error.message", ex.message)
    session.transfer(flowFile, REL_FAILURE)
}

import org.apache.commons.io.IOUtils
import java.nio.charset.StandardCharsets
import groovy.json.JsonOutput
import java.util.Base64

def flowFile = session.get()
if (!flowFile) return

try {

    // c ton b XML input
    def xmlText = ''
    session.read(flowFile, { inputStream ->
        xmlText = IOUtils.toString(inputStream, StandardCharsets.UTF_8)
    } as org.apache.nifi.processor.io.InputStreamCallback)

    // Encode Base64
    def base64Xml = Base64.encoder.encodeToString(
            xmlText.getBytes(StandardCharsets.UTF_8)
    )

    // Build JSON output
    def jsonMap = [
        file_Content: base64Xml
    ]

    def jsonString = JsonOutput.prettyPrint(
            JsonOutput.toJson(jsonMap)
    )

    // Ghi li content FlowFile = JSON
    flowFile = session.write(flowFile, { out ->
        out.write(jsonString.getBytes(StandardCharsets.UTF_8))
    } as org.apache.nifi.processor.io.OutputStreamCallback)

    // Set mime type
    flowFile = session.putAttribute(flowFile, "mime.type", "application/json")

    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception e) {
    log.error("Base64 encode XML error", e)
    flowFile = session.putAttribute(flowFile, "error.message", e.message)
    session.transfer(flowFile, REL_FAILURE)
}

