                    // --- PHN KHC BIT: DanhSachDanhMuc ---
                    DanhSachDanhMuc {
                        if (json.DanhSachDanhMuc instanceof List) {
                            json.DanhSachDanhMuc.each { item ->
                                DuLieuDanhMuc {

                                    // TRNG HP 1: ten_bang l danhmucdkd
                                    if (tenBang == 'danhmucdkd') {
                                        Id(item.dl_Id ?: "")
                                        MaDKD(item.dl_MaDKD ?: "")
                                        TenDKD(item.dl_TenDKD ?: "")
                                        Dieu(item.dl_Dieu ?: "")
                                        Khoan(item.dl_Khoan ?: "")
                                        Diem(item.dl_Diem ?: "")
                                        ThongTuNghiDinh(item.dl_ThongTuNghiDinh ?: "")
                                        TrangThai(item.dl_TrangThai ?: "")
                                        MoTa(item.dl_MoTa ?: "")
                                        MaCha(item.dl_MaDanhMucCha ?: "")
                                        MaPhienBan(item.dl_MaPhienBan ?: "")
                                        LoaiBanHanh(item.dl_LoaiBanHanh ?: "")
                                        NgayHieuLuc(item.dl_NgayHieuLuc ?: "")
                                        NgayHetHieuLuc(item.dl_NgayHetHieuLuc ?: "")
                                        MaDanhMucCu(item.dl_MaDanhMucCu ?: "")
                                    } 
                                    // TRNG HP 2: ten_bang thuc nhm 4 bng kia
                                    else if (bangNhom2.contains(tenBang)) {
                                        Id(item.dl_Id ?: "")
                                        Ma(item.dl_Ma ?: "")
                                        GiaTri(item.dl_GiaTri ?: "")            
                                        TrangThai(item.dl_TrangThai ?: "")
                                        MoTa(item.dl_MoTa ?: "")
                                        MaCha(item.dl_MaDanhMucCha ?: "")
                                        MaPhienBan(item.dl_MaPhienBan ?: "")
                                        LoaiBanHanh(item.dl_LoaiBanHanh ?: "")
                                        NgayHieuLuc(item.dl_NgayHieuLuc ?: "")
                                        NgayHetHieuLuc(item.dl_NgayHetHieuLuc ?: "")
                                        MaDanhMucCu(item.dl_MaDanhMucCu ?: "")
                                    }
                                    // TRNG HP KHC (Optional): X l nu ten_bang khng khp ci no
                                    else {
                                       // C th  trng hoc log warning, hin ti ang map cc trng chung nht
                                       Id(item.dl_Id ?: "")
                                       TrangThai(item.dl_TrangThai ?: "")
                                    }
                                }
                            }
                        }
                    }
                }
            }
        }

        // 4. Ghi kt qu ra Output
        outputStream.write(xml.toString().getBytes(StandardCharsets.UTF_8))

    } as org.apache.nifi.processor.io.StreamCallback)

    // Cp nht mime.type thnh application/xml
    flowFile = session.putAttribute(flowFile, "mime.type", "application/xml")
    session.transfer(flowFile, REL_SUCCESS)

} catch (Exception ex) {
    log.error("Li chuyn i JSON sang XML: " + ex.message)
    flowFile = session.putAttribute(flowFile, "error.message", ex.message)
    session.transfer(flowFile, REL_FAILURE)
}
